(57, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-11-13 04:58:12', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2023-11-13 06:48:27', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2023-11-13 04:58:12', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-11-13 04:58:12', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2023-11-13 04:58:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2023-11-14 08:01:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2023-11-14 08:01:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2023-11-18 16:19:35', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2023-11-18 16:19:35', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help WooCommerce improve with usage tracking', 'Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://localhost:82/wordpress/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">Read more</a> about what data we collect.', '{}', 'unactioned', 'woocommerce-admin', '2023-11-21 08:25:49', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'wc-admin-new-sales-record', 'info', 'en_US', 'New sales record!', 'Woohoo, 23 Tháng Mười Một, 2023 was your record day for sales! Net sales was ₫350.00 beating the previous record of ₫29.00 set on 13 Tháng Mười Một, 2023.', '{\"old_record_date\":\"2023-11-13\",\"old_record_amt\":29,\"new_record_date\":\"2023-11-23\",\"new_record_amt\":350}', 'unactioned', 'woocommerce-admin', '2023-11-24 06:03:57', NULL, 0, 'plain', '', 0, 0, 'info'),
